// --- HELPER FUNCTIONS ---
const getMarkerIcon = (riskLevel) => {
    if (riskLevel === 1) return "http://maps.google.com/mapfiles/ms/icons/green-dot.png";
    if (riskLevel === 2) return "http://maps.google.com/mapfiles/ms/icons/yellow-dot.png";
    if (riskLevel === 3) return "http://maps.google.com/mapfiles/ms/icons/orange-dot.png";
    return "http://maps.google.com/mapfiles/ms/icons/red-dot.png";
};

const getRiskText = (level) => {
    switch(level) {
        case 1: return '<span style="color: green;">Low</span>';
        case 2: return '<span style="color: darkorange;">Medium</span>';
        case 3: return '<span style="color: red;">High</span>';
        case 4: return '<span style="color: darkred;">Very High</span>';
        default: return 'Unknown';
    }
};

function DisasterMap() {
    const mapRef = useRef(null); // A reference to the div where the map will be rendered
    const [map, setMap] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    // ====================================================================
    // IMPORTANT: PASTE YOUR GOOGLE MAPS API KEY HERE!
    // ====================================================================
    const API_KEY = "AIzaSyAXuZiBgzAAftj2BsZjXxMB8e31m_rQHFU"; // This is the key from your HTML file

    useEffect(() => {
        if (!API_KEY || API_KEY === "YOUR_API_KEY_GOES_HERE") {
            console.error("CRITICAL: Please add your Google Maps API Key to DisasterMap.js");
            setIsLoading(false);
            return;
        }

        // This function will be called by the Google Maps script once it's loaded
        window.initMap = () => {
            if (mapRef.current) {
                const googleMap = new window.google.maps.Map(mapRef.current, {
                    center: center,
                    zoom: 5,
                });
                setMap(googleMap);
                setIsLoading(false);
            }
        };

        // If the Google Maps script isn't already on the page, this adds it
        if (!window.google) {
            const script = document.createElement('script');
            script.src = `https://maps.googleapis.com/maps/api/js?key=${API_KEY}&callback=initMap`;
            script.async = true;
            script.defer = true;
            document.head.appendChild(script);
        } else {
            // If the script is somehow already there, just initialize the map
            window.initMap();
        }

        // Cleanup function to remove the script when the component is no longer on screen
        return () => {
            const scriptTag = document.querySelector(`script[src*="maps.googleapis.com"]`);
            if (scriptTag) {
                document.head.removeChild(scriptTag);
            }
            if (window.initMap) {
                delete window.initMap;
            }
        };
    }, []); // The empty array [] ensures this effect only runs once

    // This effect runs after the `map` object has been created and plots the markers
    useEffect(() => {
        if (map) {
            const infowindow = new window.google.maps.InfoWindow();

            disasterData.forEach(place => {
                const locationName = place.name || place.state;
                const marker = new window.google.maps.Marker({
                    position: { lat: place.lat, lng: place.lng },
                    map: map,
                    title: locationName,
                    icon: { url: getMarkerIcon(place.riskLevel) }
                });

                marker.addListener('click', () => {
                    const content = `<div style="font-family: sans-serif; color: #333; padding: 5px;">` +
                                  `<strong>${locationName}</strong><br>` +
                                  `<b>Risk Level:</b> ${getRiskText(place.riskLevel)}<br>` +
                                  `<b>Common Disasters:</b> ${place.disasters}</div>`;
                    
                    infowindow.setContent(content);
                    infowindow.open(map, marker);
                });
            });
        }
    }, [map]); // This effect runs whenever the 'map' state changes

    return (
            <div style={{ position: 'relative', ...containerStyle }}>
                {isLoading && (
                    <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', fontFamily: 'sans-serif' }}>
                        Loading Map...
                    </div>
                )}
                <div ref={mapRef} style={{ width: '100%', height: '100%', borderRadius: '15px' }} />
            </div>
    );
}

export default DisasterMap;

